import React, { useState } from 'react';
import './App.css';

function App() {
  const [email, setEmail] = useState('');
  const [name, setName] = useState('');
  const [userData, setUserData] = useState(null);  // Stores user data received from the server
  const [userStatus, setUserStatus] = useState(''); // Stores the user status (found or not_found)
  const [error, setError] = useState('');

  const handleEmailSubmit = async (event) => {
    event.preventDefault();
    setError('');
    try {
      const response = await fetch('/mongodbsearch', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email: email }),
      });
      const data = await response.json();
      if (response.ok) {
        setUserStatus(data.status);
        setUserData(data.data); // Saving the user data from the response
        console.log(data); // For debugging
      } else {
        throw new Error(data.error || 'Something went wrong');
      }
    } catch (err) {
      setError(err.message);
      console.error('Error:', err);
    }
  };

  const handleNameSubmit = async (event) => {
    event.preventDefault();
    // Additional implementation for submitting name to the server
  };

  return (
    <div className="App">
      <div className="App-header">
        <form onSubmit={handleEmailSubmit}>
          <label htmlFor="email">Enter your Email Address:</label>
          <input
            type="email"
            id="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <button type="submit">Submit</button>
        </form>
        {userStatus === 'not_found' && (
          <form onSubmit={handleNameSubmit}>
            <label htmlFor="name">Enter your Name:</label>
            <input
              type="text"
              id="name"
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
            />
            <button type="submit">Submit</button>
          </form>
        )}
        {userStatus === 'found' && <div>Welcome back, {userData.name}!</div>}
        {error && <div>Error: {error}</div>}
      </div>
    </div>
  );
}

export default App;
