import React, { useState } from 'react';
import './App.css';

function App() {
  const [email, setEmail] = useState('');
  const [name, setName] = useState('');
  const [stock, setStock] = useState('');
  const [company, setCompany] = useState('');
  const [userData, setUserData] = useState({});  // Now defined and initialized here
  const [userStatus, setUserStatus] = useState('');
  const [searchHistory, setSearchHistory] = useState([]);
  const [error, setError] = useState('');

  const handleEmailSubmit = async (event) => {
    event.preventDefault();
    setError('');
    try {
      const response = await fetch('/mongodbsearch', {
        method: 'POST',
        headers: {'Content-Type': 'application/json'},
        body: JSON.stringify({ email: email }),
      });
      const data = await response.json();
      if (response.ok) {
        setUserStatus(data.status);
        setName(data.data.name);  // Assuming name is in the data object
        setSearchHistory(data.data.history || []);  // Assuming history is in the data object
        setUserData(data.data); // This line sets the user data state including the ID
      } else {
        throw new Error(data.error || 'Something went wrong');
      }
    } catch (err) {
      setError(err.message);
    }
  };

  const handleNameSubmit = async (event) => {
    event.preventDefault();
    if (name.trim()) { // Ensure name is not just whitespace
      setUserStatus('name_submitted');
    }
  };

  const handleStockSubmit = async (event) => {
    event.preventDefault();
    const dateTime = new Date().toISOString();

    let endpoint, payload;
    if (userStatus === 'found') {
      // Update existing user's data
      endpoint = '/mongo_update';
      payload = {
        id: userData._id, // Now using the defined userData
        company: company,
        dateTime: dateTime
      };
    } else {
      // Create a new user document
      endpoint = '/mongodb_create';
      payload = {
        email: email,
        name: name,
        company: company,
        dateTime: dateTime
      };
    }

    try {
      const response = await fetch(endpoint, {
        method: 'POST',
        headers: {'Content-Type': 'application/json'},
        body: JSON.stringify(payload),
      });
      const result = await response.json();
      if (response.ok) {
        console.log('Submission successful', result);
        alert('Details submitted successfully!');
        // Optionally update searchHistory or redirect the user
      } else {
        throw new Error(result.error || 'Failed to submit details');
      }
    } catch (err) {
      console.error('Error:', err);
      setError(err.message);
    }
  };

  return (
    <div className="App">
      <div className="App-header">
        {userStatus === '' && (
          <form onSubmit={handleEmailSubmit}>
            <label htmlFor="email">Enter your Email Address:</label>
            <input
              type="email"
              id="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
            <button type="submit">Submit</button>
          </form>
        )}

        {userStatus === 'not_found' && (
          <form onSubmit={handleNameSubmit}>
            <label htmlFor="name">Enter your Name:</label>
            <input
              type="text"
              id="name"
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
            />
            <button type="submit">Submit</button>
          </form>
        )}

        {(userStatus === 'found' || userStatus === 'name_submitted') && (
          <>
            <h2>Welcome {userStatus === 'found' ? 'Back' : ''}, {name}!</h2>
            <form onSubmit={handleStockSubmit}>
              <label htmlFor="stock">Enter Stock Symbol:</label>
              <input
                type="text"
                id="stock"
                value={stock}
                onChange={(e) => setStock(e.target.value)}
                required
              />
              <label htmlFor="company">Enter Company Name:</label>
              <input
                type="text"
                id="company"
                value={company}
                onChange={(e) => setCompany(e.target.value)}
                required
              />
              <button type="submit">Submit</button>
            </form>
            <div className="search-history">
              {searchHistory.length > 0 && (
                <>
                  <h3>Quick History</h3>
                  {searchHistory.map((item, index) => (
                    <div key={index}>{item.date} - {item.company}</div>
                  ))}
                </>
              )}
            </div>
          </>
        )}

        {error && <div>Error: {error}</div>}
      </div>
    </div>
  );
}

export default App;
