import React, { useState } from 'react';
import './App.css';

function App() {
  const [email, setEmail] = useState('');
  const [name, setName] = useState('');
  const [stock, setStock] = useState('');
  const [company, setCompany] = useState('');
  const [userStatus, setUserStatus] = useState('');
  const [searchHistory, setSearchHistory] = useState([]);
  const [error, setError] = useState('');

  const handleEmailSubmit = async (event) => {
    event.preventDefault();
    setError('');
    try {
      const response = await fetch('/mongodbsearch', {
        method: 'POST',
        headers: {'Content-Type': 'application/json'},
        body: JSON.stringify({ email: email }),
      });
      const data = await response.json();
      if (response.ok) {
        setUserStatus(data.status);
        setUserData(data.data);
        setName(data.data.name);
        setSearchHistory(data.data.history || []);
      } else {
        throw new Error(data.error || 'Something went wrong');
      }
    } catch (err) {
      setError(err.message);
    }
  };

  const handleNameSubmit = async (event) => {
    event.preventDefault();
    if (name.trim()) { // Ensure name is not just whitespace
      try {
        const response = await fetch('/submit-name', {
          method: 'POST',
          headers: {'Content-Type': 'application/json'},
          body: JSON.stringify({ email: email, name: name }),
        });
        if (!response.ok) throw new Error('Failed to save name');
        setUserStatus('name_submitted');
      } catch (err) {
        setError(err.message);
      }
    }
  };

  return (
    <div className="App">
      <div className="App-header">
        {!userStatus && (
          <form onSubmit={handleEmailSubmit}>
            <label htmlFor="email">Enter your Email Address:</label>
            <input
              type="email"
              id="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
            <button type="submit">Submit</button>
          </form>
        )}

        {userStatus === 'not_found' && !name && (
          <div>
            <h2>You are New Here</h2>
            <form onSubmit={handleNameSubmit}>
              <label htmlFor="name">Enter your Name:</label>
              <input
                type="text"
                id="name"
                value={name}
                onChange={(e) => setName(e.target.value)}
                required
              />
              <button type="submit">Submit</button>
            </form>
          </div>
        )}

        {userStatus === 'found' && (
          <>
            <h2>Welcome Back, {name}!</h2>
            <form onSubmit={handleStockSubmit}>
              <label htmlFor="stock">Enter Stock Symbol:</label>
              <input
                type="text"
                id="stock"
                value={stock}
                onChange={(e) => setStock(e.target.value)}
                required
              />
              <label htmlFor="company">Enter Company Name:</label>
              <input
                type="text"
                id="company"
                value={company}
                onChange={(e) => setCompany(e.target.value)}
                required
              />
              <button type="submit">Submit</button>
            </form>
            {searchHistory.length > 0 && (
              <div className="search-history">
                <h3>Quick History</h3>
                {searchHistory.map((item, index) => (
                  <div key={index}>{item.date} - {item.company}</div>
                ))}
              </div>
            )}
          </>
        )}

        {userStatus === 'name_submitted' && (
          <>
            <h2>Welcome, {name}!</h2>
            <form onSubmit={handleStockSubmit}>
              <label htmlFor="stock">Enter Stock Symbol:</label>
              <input
                type="text"
                id="stock"
                value={stock}
                onChange={(e) => setStock(e.target.value)}
                required
              />
              <label htmlFor="company">Enter Company Name:</label>
              <input
                type="text"
                id="company"
                value={company}
                onChange={(e) => setCompany(e.target.value)}
                required
              />
              <button type="submit">Submit</button>
            </form>
          </>
        )}

        {error && <div>Error: {error}</div>}
      </div>
    </div>
  );
}

export default App;
